import React, { Component } from 'react'
import API from '../../utils/API'
export default class LikeButton extends Component {
    state={
        clasName:""
    }
    componentDidMount=()=>{
        console.log(this.props.clsName)
        this.setState({clasName:this.props.clasName})
        let result = []
        let user_obj = JSON.parse(localStorage.getItem('okta-token-storage'))
        let snippets_liked_by_this_user=[]
        var promise1 = new Promise(function(resolve, reject) {

            API.getLikes()
            .then(result=>{
                resolve(result.data);
        })

      });

      promise1.then(function(value) {
        result = value
        for(let i=0;i<result.length;i++){
            if(result[i].author_id === user_obj.idToken.clientId){
                snippets_liked_by_this_user.push(result[i].snippet_id)
            }
        }
      });
      let i=0
      for( i=0;i<snippets_liked_by_this_user.length;i++){
          if(snippets_liked_by_this_user[i] === this.props.snippet_id){
              this.setState({clasName:"btn btn-secondary"})
              break
          }
      }
      if(i<snippets_liked_by_this_user.length){
          this.setState({clasName:"btn btn-secondary"})
      }
    }
    changeClass=()=>{

        this.props.likeThisSnippet(this.props.snippet_id)
    }
  render() {
    return (
        <button style={{"marginRight":"5px"}}  type="button" className={this.state.clasName} onClick={this.changeClass}>Like</button>

    )
  }
}
